package day2;

public class Person {
 
	  private String dob;
	  private String name;

	  
	  public Person() {
		// TODO Auto-generated constructor stub
	}
	  
	  public Person(String dob, String name) {
	
		this.dob = dob;
		this.name = name;
	}
	public String getDob() {
		return dob;
	}
	public void setDob(String dob) {
		this.dob = dob;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	  
	  
	  
}
